  if(data.length>10){
    var newcontent="";
    for (var i=0; i <=5 ; i++) { 
      newcontent+=data[i]+"<br>";
    }
    for (i=data.length-7; i>5 ; i--) { 
      newcontent+=data[i]+"<br>";
    }
    for (i=data.length-6; i <data.length ; i++) { 
      newcontent+=data[i];
      if(i!=data.length-1){
        newcontent+="<br>";
      }
    }
    newcontent=String(newcontent).replace(/<br>/g, "");
    result=newcontent
  }else{
    result=content
 }

