ruleToc.nextTocUrl:
@js:
var n=result.match(/第\d+\/(\d+)页/)[1];
var m=baseUrl.match(/(\d+\/\d+)\//)[1];
var list=[];
for(var i=2;i<=n;i++){list.push(baseUrl.replace(/\d+\/\d+\//,'')+m+'_'+i+'/');}
list

ruleToc.chapterList:
@js:
var data=result.match(/<\/dt>[^@]*<\/dt>([^@]*)<\/dl>/)[1];//只取有用的部分
var datalist=data.split("<dd>");//数据切开
var list=[],len=datalist.length;
if(len>1){
	for(var i=1;i<len;i++){
		var temp=datalist[i].match(/<a href="(.*)">(.*)<\/a>/);
		list.push({
			"chapterName":temp[2],
			"chapterUrl":temp[1]
		});
	}
}
list;

