@js:
if(result.match(/class="rtcspage/)){
doc=org.jsoup.Jsoup.parse(result.replace(/<\/*span.*?>|<\/*section.*?>/g,'')).select('.rtcspage');
if(result.match(/>\..*?\{display: none\;\}</)){
a=result.match(/>(\..*?)\{display: none\;\}</)[1];
doc.select(a).remove();doc.select("style").remove();
;result=doc.html()}else{
result=doc.html()
}
String(result).replace(/来自/,'').replace(/百度/,'').replace(/文库/,'')
}else{
if(result.match(/class="txt/)){
org.jsoup.Jsoup.parse(result).select(".content .txt").html()}
else{
md5=result.match(/"md5sum":"([^"]+)"/)[1];
token=result.match(/"downloadToken":"([^"]+)"/)[1];
imglist=eval(result.match(/"bcsParam":(\[[\s\S]+?\])/)[1]);
html='';
for(i in imglist){
url='https://wkretype.bdimg.com/retype/zoom/'+token+'?pn='+parseInt(imglist[i].page)+'&x=0&y=0&raww=1080&rawh=810&o=jpg_6&type=pic&aimh=810'+md5+imglist[i].zoom;
html+='<img src="'+url+'">\n'
}
result=html
}
}

